public class Day06 {

    static String in = "";
    static String inTest = "mjqjpqmgbljsphdztnvjfqwrcgsmlb";
    static int tokenLen = 4;
    static int secondTokenLen = 14;

    public static void main(String[] args) {
        int i = 0;
        String s = in;
        while(!allCharsDifferent(s.substring(i, i+tokenLen))) {
            i++;
        }
        System.out.printf("SUCCESS! %n%s at %d%n", s.substring(i, i+tokenLen), i+tokenLen);


        while(!allCharsDifferent(s.substring(i, i+secondTokenLen))) {
            i++;
        }
        System.out.printf("SUCCESS! %n%s at %d%n", s.substring(i, i+secondTokenLen), i+secondTokenLen);
    }

    private static boolean allCharsDifferent(String s) {
        for (int j = 0; j < s.length(); j++) {
            for (int k = 0; k < s.length(); k++) {
                if(j!=k && s.toCharArray()[j] == s.toCharArray()[k]) return false;
            }
        }
        return true;
    }

}
